mapper.select:
select username, userid, email
from ssafy_member
where userid = #{userid} and userpwd = #{userpwd}

select username, userid, email, joindate
from ssafy_member
where userid = #{userid}

select userid, username, userpwd, email, phone, date_format(regdate, '%y.%m.%d') regdate
from userinfo
order by regdate desc;

select userid, username, userpwd, email, phone, date_format(regdate, '%y.%m.%d') regdate
from userinfo
where userid = #{userId}

mapper.update:
